IFS=$'\n'; for i in $(influx -format csv -username admin -password <admin_pw> -database _internal -execute "show subscriptions" | tail -n +2 | grep -v name); do influx -format csv -username admin -password <admin_pw> -database _ -execute "drop subscription \"$(echo "$i" | cut -f 3 -d ',')\" ON \"$(echo "$i" | cut -f 1 -d ',')\".\"$(echo "$i" | cut -f 2 -d ',')\""; done
